from zstacklib.utils import httpfrom zstacklib.utils import logfrom zstacklib.utils import threadlogger = log.get_logger(__name__)class ProgressReportCmd(object):    def __init__(self):        self.progress = None        self.processType = None        self.resourceUuid = None        self.serverUuid = Noneclass Report(object):    url = None    serverUuid = None    def __init__(self):        self.resourceUuid = None        self.progress = None        self.header = None        self.processType = None    @thread.AsyncThread    def report(self):        cmd = ProgressReportCmd()        cmd.serverUuid = Report.serverUuid        cmd.processType = self.processType        cmd.progress = self.progress        cmd.resourceUuid = self.resourceUuid        logger.debug("url: %s, cmd: %s, header: %s", Report.url, cmd, self.header)        http.json_dump_post(Report.url, cmd, self.header)